SELECT
*
FROM
(
SELECT
/*+INDEX_DESC(COMMUNITY COMMUNITY_PK)*/
ROWNUM RN, COMM_NO, COMM_TITLE, COMM_CONTENT,
ID, COMM_DATE,COMM_HIT
FROM COMMUNITY
where



ROWNUM <= #{pageNum} * #{amount} 
)
WHERE RN > (#{pageNum} -1) * #{amount}


SELECT
*
FROM
(
SELECT
/*+INDEX_DESC(COMMUNITY COMMUNITY_PK)*/
ROWNUM RN, COMM_NO, COMM_TITLE, COMM_CONTENT,
COMM_DATE,COMM_HIT
FROM COMMUNITY
where



ROWNUM <= #{pageNum} * #{amount} 
AND ID = #{id}
)
WHERE RN > (#{pageNum} -1) * #{amount}
