mapper.select:

SELECT
*
FROM
(
SELECT
/*+INDEX_DESC(COMMUNITY COMMUNITY_PK)*/
ROWNUM RN, COMM_NO, COMM_TITLE, COMM_CONTENT,
ID, COMM_DATE
FROM COMMUNITY
where



ROWNUM <= #{pageNum} * #{amount} 
)
WHERE RN > (#{pageNum} -1) * #{amount}

mapper.update:
