mapper.insert:

	SELECT 'gym_' || GYM_SEQ.NEXTVAL FROM DUAL

INSERT INTO GYM ( 
	  GYM_NO
	, PAY_NO
	, GYM_STARTDATE
	, GX_CODE
	, GYM_PERIOD
	, GYM_PRICE 
)
VALUES (
	  #{gymNo}
	, #{payNo}
	, #{gymStartdate}
	, #{gxCode}
	, #{gymPeriod}
	, #{gymPrice}
)


	SELECT 'lib_' || LIB_SEQ.NEXTVAL FROM DUAL

INSERT INTO LIBRARY (
	  LIB_NO
    , PAY_NO
    , LIB_STARTDATE
    , LIB_PERIOD
    , LIB_PRICE 
)
VALUES (
	  #{libNo}
	, #{payNo}
	, #{libStartdate}
	, #{libPeriod}
	, #{libPrice}
)

INSERT INTO PAYMENT (
	  PAY_NO
	, ID
	, PAY_TYPE
	, PAY_CAT
	, PAY_STATUS
)
VALUES (
	  #{payNo}
	, #{id}
	, #{payType}
	, #{payCat}
	, #{payStatus}
)
