mapper.insert:

	SELECT 'lib_' || LIB_SEQ.NEXTVAL FROM DUAL

INSERT INTO LIBRARY (
	  LIB_NO
    , PAY_NO
    , LIB_STARTDATE
    , LIB_PERIOD
    , LIB_PRICE 
)
VALUES (
	  #{libNo}
	, #{payNo}
	, #{libStartdate}
	, #{libPeriod}
	, #{libPrice}
)

INSERT INTO PAYMENT ( 
	  PAY_NO
    , ID
    , PAY_DATE
    , PAY_TYPE
    , PAY_CAT
    , PAY_STATUS 
)
VALUES (
	  #{payNo}
	, #{id}
	, #{payDate}
	, #{payType}
	, #{payCat}
	, #{payStatus}
)
