mapper.insert:

	SELECT OI_SEQ.NEXTVAL FROM DUAL


INSERT INTO OPERATION_INFO (
	   OI_NO
	 , OI_TYPE
	 , OI_TITLE
	 , OI_CONTENT
	 , OI_DATE
	 , OI_UPDATE
	 , OI_FILENAME
	 , OI_FILEID
	 , OI_FILEPATH
) VALUES (
	   #{oiNo}
	 , #{oiType}
	 , #{oiTitle}
	 , #{oiContent}
	 , SYSDATE
	 , SYSDATE
	 , #{oiFilename}
	 , #{oiFileid}
	 , #{oiFilepath}
)

mapper.update:
UPDATE OPERATION_INFO
SET OI_TITLE = #{oiTitle}
  , OI_CONTENT = #{oiContent}
  , OI_UPDATE = SYSDATE
  , OI_FILENAME = #{oiFilename}
  , OI_FILEID = #{oiFileid}
  , OI_FILEPATH = #{oiFilepath}
WHERE OI_NO = #{oiNo}
