mapper.select:
SELECT COUNT(*)
FROM MEMBER
WHERE ID=#{id}

SELECT HOUSE_INFO
FROM GENERATION
WHERE AUTH_KEY = #{authKey}

SELECT ID, NAME, PHONE
FROM MEMBER
WHERE NAME=#{name}
AND PHONE=#{phone}

SELECT count(*)
FROM MEMBER
WHERE NAME=#{name}
AND PHONE=#{phone}
AND ID=#{id}

mapper.update:
UPDATE MEMBER
SET PASSWORD=#{password}
WHERE ID=#{id}
